all_by_pois.map:
function (doc, meta) {
  if( doc.puid )
    // delete pois
  	emit(doc.puid, meta.id);
  else if( doc.pois_a && doc.pois_b && doc.pois_a != doc.pois_b){
    // delete connections
  	emit( doc.pois_a, meta.id );
    	emit( doc.pois_b, meta.id );
  }
}

all_by_floor.map:
function (doc, meta) {
  if( doc.buid && doc.floor_number )
    // delete pois & floor
  	emit( [doc.buid, doc.floor_number], meta.id);
  else if( doc.floor_a && doc.floor_b && doc.floor_a != doc.floor_b){
    // delete connections between floors
  	emit( [doc.buid, doc.floor_a], meta.id );
    	emit( [doc.buid, doc.floor_b], meta.id );
  }else if( doc.floor_a && doc.floor_b && doc.floor_a == doc.floor_b){
    // connections on the same floor
    	emit( [doc.buid, doc.floor_a], meta.id );
  }
}

all_by_buid.map:
function (doc, meta) {
  if( doc.buid )
    // delete pois & floor & building & connection
  	emit( doc.buid, meta.id);

}

building_all_by_owner.map:
function (doc, meta) {
  if(!doc.puid && !doc.floor_number && !doc.edge_type && doc.buid && doc.owner_id) {
    emit(doc.owner_id, meta.id);

    if(doc.co_owners) {
      for (var i = 0; i < doc.co_owners.length; i++) {
        if (doc.co_owners[i] != doc.owner_id) {
           emit(doc.co_owners[i], meta.id);
        }
      }
    }

  }
}

